import React, {useState, useEffect, useRef} from 'react';
import { View, Text, StyleSheet, TouchableOpacity,Image, TextInput, ScrollView} from 'react-native';
import stylesB from '../assets/css/stylesB'
import { ScreenNavigationProp } from '../services/interfaces/navigationTypes';
import Icon from 'react-native-vector-icons/FontAwesome';



type Props = {
  navigation: ScreenNavigationProp;
};

interface CategoryStyles {
  [category: string]: object;
}

const HomeFage2 = ({ navigation }: Props) => {
  const categories: string[] = ['Pizza', 'Burger', 'Hotdog', 'Tea', 'juice'];
  const [selectedCategory, setSelectedCategory] = useState<string>('Pizza');
  const [indicatorPosition, setIndicatorPosition] = useState<number>(0);



  const handleCategoryPress = (category: string, index: number): void => {
    setSelectedCategory(category);
    const position = index * 120; // Giả định mỗi danh mục có chiều rộng cố định là 120
    setIndicatorPosition(position);
  };

    return(
      <View style={[stylesB.container, {}]}>
        <TouchableOpacity style={styles.logOut}>
          <Text style={styles.textLogOut}>Đăng xuất</Text>  
        </TouchableOpacity>
        <Text style={styles.textTitle}>Tìm các món ăn bạn thèm nhất!</Text>
        <View style={styles.containerSearch}>
          <TextInput  style={[, styles.inputSearch]} placeholder="Tìm món ăn" autoCapitalize='none'/>
          <Icon style={styles.iconSearch} name="search" size={30}/>
        </View>
        <View style={styles.menus}>
        <ScrollView horizontal>
          {categories.map((category: string, index: number) => (
            <TouchableOpacity
              key={category}
              onPress={() => handleCategoryPress(category, index)}
              style={[
                styles.category,
                selectedCategory === category ? styles.selected : null,
              ]}
            >
              <Text>{category}</Text>
            </TouchableOpacity>
          ))}
          <View style={[styles.selected, { left: indicatorPosition }]} />
        </ScrollView>
        </View>
      </View>

    )
}

const styles = StyleSheet.create({
  logOut:{
    borderColor: '#F24822',
    borderWidth: 2,
    borderRadius: 14,
    width: 100,
    height:40,
    justifyContent:'center',
    alignItems:'center',
    position:'absolute',
    right:0,
    marginRight:10,
  },
  textLogOut:{
    color:'#F24822',
    fontWeight: '700',
    fontSize:18
  },
  textTitle:{
    marginTop: '12%',
    fontSize:30,
    width:260,
    fontWeight:'700',
    textAlign:'center'
  },
  containerSearch:{
    marginTop:80,
    marginBottom:30,
    width:320,
    height: 44,
  },
  inputSearch:{
    borderRadius:18,
    backgroundColor:'#E6E6E6',
    paddingLeft:30,
    fontSize:18,
    flex:1
  },
  iconSearch:{
    position:'absolute',
    right:0,
    height:40,
    width:40,
    paddingTop:6
  },
  menus:{
    width:'100%',

  },
  categoryBar:{
    flexDirection:'row',
  },
  category:{
    paddingRight:10,
    paddingLeft:10,
    fontSize:30,
    fontWeight:'500',
    height:50,
    width:120,
    textAlign:'center'
  },
  selected:{
    height:5,
    backgroundColor:'#F24822',
    borderRadius:5,
    bottom:0,
    position:'absolute'
  },

})

export default HomeFage2
