import React, { useState } from 'react';
import { useEffect } from 'react';
import { View, Text, TextInput, StyleSheet, Image, TouchableOpacity, ActivityIndicator, Alert, KeyboardAvoidingView} from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import stylesB from '../assets/css/stylesB'
import { signIn } from '../services/Auth';
import { validateEmail, validatePassword }from '../utils/Validate';
import { ScreenNavigationProp } from '../services/interfaces/navigationTypes';
import AsyncStorage from '@react-native-async-storage/async-storage';


type Props = {
  navigation: ScreenNavigationProp;
};


const LoginScreen = ({ navigation }: Props) => {
  const [email, setEmail] = useState(''); // Khởi tạo state cho email
  const [password, setPassword] = useState(''); // Khởi tạo state cho password
  const [loading, setLoading] = useState(false);

  const [isChecked, setIsChecked] = useState(false);




  const checkmarkIcon = <Icon name="check" size={16} color="blue" />;


    const handleSignIn = async () => {
      setLoading(true)
      try {
        if (!validateEmail(email)) {
          // Kiểm tra địa chỉ email bằng hàm validateEmail
          Alert.alert('Lỗi', 'Địa chỉ email không hợp lệ.');
          setLoading(false);
          return;
        }
        if(!validatePassword(password)){
          Alert.alert('Lỗi', 'Mật khẩu phải lớn hơn 5 kí tự.');
          setLoading(false);
          return;
        }
        const user = await signIn(email , password);
        // Xử lý sau khi đăng nhập thành công
        navigation.navigate('HomeFage1')
        // Lưu trạng thái đăng nhập sau khi thành công
        if (isChecked) {
          await AsyncStorage.setItem('isLoggedIn', 'true');
        } else {
          // Nếu isChecked === false, xóa trạng thái đăng nhập

          await AsyncStorage.removeItem('isLoggedIn');
        }

      } catch (error) {
        // Xử lý lỗi đăng nhập
        alert('Đăng nhập không thành công, vui lòng kiểm tra lại tài khoản và mật khẩu')
      }finally{
        setLoading(false)
      };
    }

    // const nav = useNavigation()
useEffect(() => {
  // Kiểm tra trạng thái đăng nhập khi mở ứng dụng
  const checkLoginStatus = async () => {
    const isLoggedIn = await AsyncStorage.getItem('isLoggedIn');
    if (isLoggedIn === 'true') {
      navigation.navigate('HomeFage2');
    }
  };
  checkLoginStatus();
}, [navigation]);

const toggleCheckbox = () => {
  setIsChecked(!isChecked);
};

  return (
    <View  style={stylesB.container}>
      <View style={styles.containerLogo}>
        <Image style={styles.logo} source={require('../assets/photoInScreens/FoodKart-login.jpg')} />
      </View>
      <KeyboardAvoidingView behavior='height' style={styles.wrapper}>
        <TextInput value={email} style={stylesB.textInput} placeholder="Tài khoản" autoCapitalize='none' 
        onChangeText={(text) => setEmail(text)} />
        <TextInput value={password} style={stylesB.textInput} placeholder="Mật khẩu"  autoCapitalize='none' secureTextEntry={true}
        onChangeText={(text) => setPassword(text)} // Cập nhật state của mật khẩu
         />
      </KeyboardAvoidingView>
      {loading ?(
         <ActivityIndicator size="large" color="#0000ff" />
      ) : (
        <>
          <TouchableOpacity  style={stylesB.containerButton}
           onPress={handleSignIn}>
              <Text style={stylesB.actionButtonText}>Đăng nhập</Text>

          </TouchableOpacity>
        </> 
      )}
      <TouchableOpacity onPress={toggleCheckbox} style={styles.checkboxContainer}>
      <View
        style={[
          styles.checkbox,
          isChecked ? styles.checked : styles.unchecked,
        ]}
      >
        {isChecked && (
      <Text >{checkmarkIcon}</Text>
      )}
      </View>
      <Text style={styles.label}>Ghi nhớ đăng nhập?</Text>
      </TouchableOpacity>
      <View style={{flexDirection:'row'}} >
        <Text style={styles.label}>Chưa đăng kí? Đăng kí tại </Text>
        <TouchableOpacity  onPress={() => navigation.navigate('Register')}>
          <Text style={stylesB.textDirectional}>đây</Text>
        </TouchableOpacity>
      </View>
      <TouchableOpacity onPress={() => navigation.navigate('HelpFage')}>
        <Text style={[styles.textHelp,stylesB.textDirectional]} >Cần trợ giúp</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({

    containerLogo:{
      marginTop:'15%',
      marginBottom:60,

    },
    logo:{
      width:468,
      height:459,
    },
    wrapper:{
      marginTop:'-30%'
    },

    checkboxContainer: {
      flexDirection: 'row',
      // alignItems: 'center',
    },
    checkbox: {

      width: 20,
      height: 20,
      borderWidth: 2,
      borderColor: '#E6E6E6',
      borderRadius: 10,
      marginRight: 10,

    },
    checked: {
      marginTop: 2,
      marginLeft: 3,
      width: 20,
      height: 20,
    },
    unchecked: {
      backgroundColor:'#E6E6E6',
      marginTop: 2,
      marginLeft: 3,
      width: 20,
      height: 20,
    },

    label: {
      fontSize: 18,
      marginBottom:10
    },
    textHelp:{
      marginTop:70
    },

})


export default LoginScreen;
