import React, {useState, useEffect, useRef} from 'react';
import { View, Text, StyleSheet, TouchableOpacity,Image,FlatList, TextInput, ScrollView, LayoutAnimation} from 'react-native';
import stylesB from '../assets/css/stylesB'
import {  Props } from '../services/interfaces/navigationTypes';
import Icon from 'react-native-vector-icons/FontAwesome';





const HomePage2 = ({ navigation }: Props) => {
  const categoryDatas = [
    {
      category: 'Pizza',
      items: [
        {name:'Pizza Margherita', imageURL:'../assets/photoScreens/food.png'},
        {name:'Pizza hải sản' , imageURL:'../assets/photoScreens/food.png'},
        {name:'Pizza thập cẩm', imageURL:'../assets/photoScreens/food.png'},
        {name:'Pizza nấm', imageURL:'../assets/photoScreens/food.png'},
        {name:'Pizza cay', imageURL:'../assets/photoScreens/food.png'},
        {name:'Pizza bò', imageURL:'../assets/photoScreens/food.png'},
        {name:'Pizza gà', imageURL:'../assets/photoScreens/food.png'},
        {name:'Pizza rau', imageURL:'../assets/photoScreens/food.png'}
      ]
    },
    {
      category: 'Burger',
      items: [{name:'Burger gà', imageURL:'../assets/photoScreens/food.png'},
       {name:'Burger bò', imageURL:'../assets/photoScreens/food.png'},
        {name:'Burger rau', imageURL:'../assets/photoScreens/food.png'}],
    },
    {
      category: 'Hotdog',
      items: [{name:'Hotdog truyền thống', imageURL:'../assets/photoScreens/food.png'},
       {name:'Hotdog gà', imageURL:'../assets/photoScreens/food.png'}],
    },
    {
      category: 'Tea',
      items: [{name:'Trà đào', imageURL:'../assets/photoScreen/food.png'},
       {name:'Trà chanh', imageURL:'../assets/photoScreen/food.png'},
        {name:'Trà xanh', imageURL:'../assets/photoScreen/food.png'}],
    },
    {
      category: 'juice',
      items: [{name:'Nước cam', imageURL:'../assets/photoScreens/food.png'},
       {name:'Nước dứa', imageURL:'../assets/photoScreens/food.png'},
      {name:'Nước táo', imageURL:'../assets/photoScreens/food.png'}],
    },
  ];
  const [selectedCategory, setSelectedCategory] = useState<string>('Pizza');

  const handleCategoryPress = (category: string): void => {
    setSelectedCategory(category);
    LayoutAnimation.configureNext(LayoutAnimation.Presets.easeInEaseOut); 
  };


  const handleHomePress = () => {
    navigation.navigate('HomePage2'); // Điều này sẽ khởi động lại màn hình Home
  };


    return(
      <View style={[stylesB.container, {}]}>
        <Text style={styles.textTitle}>Tìm các món ăn yêu thích nhất!</Text>
        <View style={styles.containerSearch}>
          <TextInput  style={[, styles.inputSearch]} placeholder="Tìm món ăn" autoCapitalize='none'/>
          <TouchableOpacity >
            <Icon style={styles.iconSearch} name="search" size={30}/>
          </TouchableOpacity>
        </View>
        <View style={styles.menus}>
          <ScrollView horizontal={true}  >
            {categoryDatas.map((categoryData) => (
              <TouchableOpacity
              key={categoryData.category} onPress={() => handleCategoryPress(categoryData.category)} 
              >
                <Text style={styles.category}>{categoryData.category}</Text>
                {selectedCategory === categoryData.category && (
                  <View style = {styles.selected}></View>
                )}
              </TouchableOpacity>
            ))}
          </ScrollView>
        </View>


          <FlatList  numColumns={2} style={styles.containerItemFood}
            data={categoryDatas.find(item => item.category === selectedCategory)?.items || []}
            keyExtractor={(item) => item.name}
            renderItem={({ item }) => (
              <TouchableOpacity style={styles.swapItemFood}>
                <Image source={{ uri: item.imageURL }} style={{ width: 100, height: 100 }} />
                <Text style={styles.itemFood} >{item.name}</Text>
              </TouchableOpacity>
            )}

          />
          <View style={styles.buttonBar}>
            <TouchableOpacity onPress={handleHomePress}>
              <Icon style={styles.iconButton} name="home" size={50} color="white"/>
            </TouchableOpacity>
            <TouchableOpacity>
              <Icon style={styles.iconButton} name="shopping-cart" size={50} color="white"/>
            </TouchableOpacity>
            <TouchableOpacity>
              <Icon style={styles.iconButton} name="file-text" size={50} color="white"/>
            </TouchableOpacity>
            <TouchableOpacity>
              <Icon style={styles.iconButton} name="user" size={50} color="white"/>
            </TouchableOpacity>

          </View>

        </View>

    )
}

const styles = StyleSheet.create({

  textTitle:{
    marginTop: '12%',
    fontSize:30,
    width:260,
    fontWeight:'700',
    textAlign:'center'
  },
  containerSearch:{
    marginTop:80,
    marginBottom:30,
    width:320,
    height: 44,
    flexDirection:'row',
    borderRadius:18,
    overflow: 'hidden'
  },
  inputSearch:{
    backgroundColor:'#E6E6E6',
    paddingLeft:30,
    fontSize:18,
    flex:1
  },

  iconSearch:{
    position:'absolute',
    right:0,
    height:40,
    width:40,
    paddingTop:6,
    paddingRight:12
  },
  menus:{
    width:'100%',

  },
  categoryBar:{
    flexDirection:'row',
  },
  category:{
    paddingRight:10,
    paddingLeft:10,
    fontSize:30,
    fontWeight:'500',
    height:50,
    width:120,
    textAlign:'center'
  },
  selected:{
    height:8,
    backgroundColor:'#F24822',
    borderRadius:5,
    bottom:0,
    position:'absolute',
    width:120,
  },
  containerItemFood:{
    position:'relative',

  },
  swapItemFood:{
    height:150,
    width:150,
    backgroundColor:'#333',
    margin:28,
    borderRadius:20
  },
  itemFood:{
  },
  buttonBar:{
    position:'relative',
    width:'100%',
    height:70,
    backgroundColor:'#F24822',
    bottom:0,
    flexDirection:'row'
  },
  iconButton:{
    marginRight:28,
    marginLeft:28,
    marginTop:6
  }

})

export default HomePage2
