import React, {useState} from 'react';
import { View, Text, StyleSheet, TouchableOpacity,Image, TextInput, ScrollView} from 'react-native';
import stylesB from '../assets/css/stylesB'
import { ScreenNavigationProp } from '../services/interfaces/navigationTypes';
import Icon from 'react-native-vector-icons/FontAwesome';



type Props = {
  navigation: ScreenNavigationProp;
};

const HomeFage2 = ({ navigation }: Props) => {
  const categories: string[] = ['Hotdog', 'Sandwich', 'Milk'];
  const [selectedCategory, setSelectedCategory] = useState<string>('Hotdog');
  const [categoryStyles, setCategoryStyles] = useState<c({
    Hotdog: styles.selected,
    Sandwich: {},
    Milk: {},
  });
  const handleCategoryPress = (category) => {
    // Xoá thuộc tính style của tất cả các thể loại
    const updatedStyles = {};
    for (const cat of categories) {
      updatedStyles[cat] = {};
    }

    // Đặt thuộc tính style cho thể loại đã chọn
    updatedStyles[category] = styles.selected;

    setSelectedCategory(category);
    setCategoryStyles(updatedStyles);
  };
    return(
      <View style={[stylesB.container, {}]}>
        <TouchableOpacity style={styles.logOut}>
          <Text style={styles.textLogOut}>Đăng xuất</Text>  
        </TouchableOpacity>
        <Text style={styles.textTitle}>Tìm các món ăn bạn thèm nhất!</Text>
        <View style={styles.containerSearch}>
          <TextInput  style={[, styles.inputSearch]} placeholder="Tìm món ăn" autoCapitalize='none'/>
          <Icon style={styles.iconSearch} name="search" size={30}/>
        </View>
        <View style={styles.menus}>
          <ScrollView horizontal={true} style={styles.categoryBar}>
            <TouchableOpacity>
              <Text style={styles.category}>Hotdog</Text>
            </TouchableOpacity>
            <TouchableOpacity>
              <Text style={styles.category}>Hotdog</Text>
            </TouchableOpacity>
            <TouchableOpacity>
              <Text style={styles.category}>Hotdog</Text>
            </TouchableOpacity>
            <TouchableOpacity>
              <Text style={styles.category}>Hotdog</Text>
            </TouchableOpacity>
            <TouchableOpacity>
              <Text style={styles.category}>Hotdog</Text>
            </TouchableOpacity>
            <View style={styles.selected}></View>
          </ScrollView>
        </View>
      </View>

    )
}

const styles = StyleSheet.create({
  logOut:{
    borderColor: '#F24822',
    borderWidth: 2,
    borderRadius: 14,
    width: 100,
    height:40,
    justifyContent:'center',
    alignItems:'center',
    position:'absolute',
    right:0,
    marginRight:10,
  },
  textLogOut:{
    color:'#F24822',
    fontWeight: '700',
    fontSize:18
  },
  textTitle:{
    marginTop: '12%',
    fontSize:30,
    width:260,
    fontWeight:'700',
    textAlign:'center'
  },
  containerSearch:{
    marginTop:80,
    marginBottom:30,
    width:320,
    height: 44,
  },
  inputSearch:{
    borderRadius:18,
    backgroundColor:'#E6E6E6',
    paddingLeft:30,
    fontSize:18,
    flex:1
  },
  iconSearch:{
    position:'absolute',
    right:0,
    height:40,
    width:40,
    paddingTop:6
  },
  menus:{
    width:'100%',

  },
  categoryBar:{
    flexDirection:'row',
  },
  category:{
    marginRight:20,
    marginLeft:16,
    fontSize:30,
    fontWeight:'500',
    height:50
  },
  selected:{
    width: 100,
    height:5,
    backgroundColor:'#F24822',
    position:'absolute',
    marginTop:40,
    margin:14,
    borderRadius:5
  }
})

export default HomeFage2
